    path: '/v3/mail/send',
    body: mail.toJSON()
  });
  sg.API(request, function (err, response) {
    if (err) {
      Logger.log("err in sendgrid: ", err);
      isEmailSent = false;
    }

    Logger.log("sendgrid body:", response.statusCode);
    Logger.log("sendgrid body:", response.headers);
    Logger.log("sendgrid body:", response.body);
    isEmailSent = true;
  });
  return isEmailSent;
}
