import User from "../register/register.model";
import jwt from "jsonwebtoken"
import {CATCH, CREATED, INTERNAL_SERVER_ERROR, NOT_FOUND, OK, UNAUTHORIZED, VERIFY} from "../../utils/status-codes";
import AppController from "../core/app.controller";

class LoginController extends AppController {


    userLogin(req, res, next) {
        const email = req.body.email;
        const password = req.body.password;
        User.findOne({email: email})
            .then(user => {
                if (!user) {
                    return res.status(NOT_FOUND).json({
                        status: false,
                        code: NOT_FOUND,
                        data: user
                    })
                }
                bcrypt.compare(password, user.password)
                    .then(isMatch => {
                        if (isMatch) {
                            const payload = {id: user.id};
                            jwt.sign(payload, app.superSecret, app.auth.expiresIn, (error, token) => {
                                return res.status(OK).json({
                                    status: true,
                                    code: OK,
                                    message: "Login success",
                                    data: {
                                        token: token,
                                        user: user
                                    }
                                })
                            })
                        }
                        if (!user.isverified) {
                            return res.status(UNAUTHORIZED).json({
                                status: false,
                                code: UNAUTHORIZED,
                                message: user.email + VERIFY
                            })
                        }
                    }).catch(error => {
                    return res.status(INTERNAL_SERVER_ERROR).json({
                        status: false,
                        message: CATCH,
                        error: error
                    })
                })
            })
    };
}

export default LoginController;

